function toTop(){var o=0;this.s=s;window.onload=sh;window.onscroll=sp;window.onresize=sp;function so(){if(window.pageYOffset>100&&o<1){document.getElementById("toTop").style.visibility="visible";o+=0.02;document.getElementById("toTop").style.o=o;setTimeout(function(){so()},20);}else if(window.pageYOffset<=100&&o>0){o-=0.02;document.getElementById("toTop").style.o=o;setTimeout(function(){so()},20);}else if(window.pageYOffset<=100&&o<=0){document.getElementById("toTop").style.visibility="hidden";}}function sp(){document.getElementById("toTop").style.left=window.innerWidth-100+"px";document.getElementById("toTop").style.top=window.innerHeight-80+"px";so();}function sh(){document.getElementById("toTop").style.position="fixed";document.getElementById("toTop").innerHTML='<div id="innerDiv" onClick="javascript:toTop.s()">â–²</div>';document.getElementById("innerDiv").style.textAlign="center";document.getElementById("innerDiv").style.lineHeight=50+"px";document.getElementById("innerDiv").style.fontSize=20+"px";document.getElementById("innerDiv").style.width=50+"px";document.getElementById("innerDiv").style.height=50+"px";document.getElementById("innerDiv").style.cursor="pointer";sp();}function s(){window.scrollTo(0,Math.floor(window.pageYOffset-window.pageYOffset/20));window.pageYOffset>1 ? setTimeout(function(){s()},10):window.scrollTo(0,0);}}toTop=new toTop();